
public class switchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String i = "abc";
		
		switch(i) {
		case "abc":
			System.out.println("case 첫번째");
			break;
		case "edf":
			System.out.println("case 두번째");
			break;
		default:
			System.out.println("default.");
		}

	}

}
