
public class MathClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(Math.pow(2, 3));    // 제곱 8.0
		System.out.println(Math.sqrt(16));    // 루트 4.0
		System.out.println(Math.abs(-10)); 	  // 절댓값 10
		System.out.println(Math.max(5, 9));   // 최댓값 9  
		System.out.println(Math.random());     // 랜덤 0이상 1미만
		 System.out.println(Math.ceil(2.3));    // 올림 3.0 
		 System.out.println(Math.floor(2.9));   // 내림 2.0
		 System.out.println(Math.round(2.5));   // 반올림 3
		 System.out.println(Math.sin(Math.PI/2)); // 삼각함 1.0

	}

}
